import React, { useState, useEffect } from 'react';
import Term from './components/Term.js'
//import Split from "react-split";

export default function App() {
    useEffect(()=>{
        document.addEventListener('keydown',SplitRight,true)
    },[])
    const SplitRight=(e)=>{
        if(e.altKey && e.shiftKey && e.key==="+") {
            console.log("SplitRight");
            document.getElementById("sRight").style.display = "";

        }
    }
    return (
        <div className='container'>
            <Split style={{height:'calc(100vh -4rem'}}>
                <div className="split left" id={"sLeft"}>
                    <Term/>
                </div>
                <div className="split right" id={"sRight"} style={{display:"none"}}>
                    <Term/>
                </div>
            </Split>
        </div>
    )
}
