import React, { useState, useEffect } from 'react';
import Term from './components/Term.js'
//import Split from "react-split";
import ReactSplit, { SplitDirection } from '@devbookhq/splitter';
export default function App() {
    useEffect(()=>{
        document.addEventListener('keydown',SplitShortKeys,true)
    },[])
    let [insertChildrenRight, setInsertChildrenRight] = useState(false)
    let [insertChildrenBottom,SetInsertChildrenBottom] = useState(false)
    const SplitShortKeys=(e)=>{
        if(e.altKey && e.shiftKey && e.key==="+") {
            console.log("SplitRight");
            if(e.key==="Escape" && e.type === 'mousedown'){
                console.log("Escape")
                setTimeout(()=>{
                    setInsertChildrenRight(false)
                }, 10)
            }
            //document.getElementById("sRight").style.display = "";
            setTimeout(()=>{
                setInsertChildrenRight(true)
            }, 10)

        }
        else if(e.altKey && e.shiftKey && e.key==="_"){
            console.log("SplitBottom");
            setTimeout(()=>{
                SetInsertChildrenBottom(true)
            }, 10)
            if(e.key==="Escape" && e.type === 'mousedown'){
                console.log("Escape")
                setTimeout(()=>{
                    SetInsertChildrenBottom(false)
                }, 10)
            }

        }

    }
    return (
        <div className='container'>
            <ReactSplit
                direction={SplitDirection.Horizontal}
                minWidths={[200, 200]}
                gutterClassName="custom-gutter"
                draggerClassName="custom-dragger"
                initialSizes={insertChildrenRight ? [50, 50] : [100]}

            >
              <Term/>
                {/*main terminal*/}
                {insertChildrenRight &&
                    <>
                        <ReactSplit
                            direction={SplitDirection.Vertical}
                            minHeights={[300, 300]}
                            gutterClassName="custom-gutter"
                            draggerClassName="custom-dragger"
                            initialSizes={insertChildrenBottom ? [50, 50] : [100]}
                        >
                            <Term/>
                            {/*SplitRight Terminal*/}
                            {insertChildrenBottom &&
                                <>
                                    <Term/>
                                    {/*Split Right Bottom Terminal*/}
                                </>
                            }
                        </ReactSplit>
                    </>
                }
            </ReactSplit>

        </div>

    )
}
