import React, { useState, useEffect } from 'react';
import Term from './components/Term.js'
//import Split from "react-split";
import ReactSplit, { SplitDirection } from '@devbookhq/splitter';
export default function App() {
    useEffect(()=>{
        document.addEventListener('keydown',SplitRight,true)
    },[])
    const SplitRight=(e)=>{
        const [sizes, setSizes] = useState([1/3 * 100, 1/3 * 100, 1/3 * 100]);
        function handleResize(gutterIdx: number, allSizes: number[]) {
                console.log('gutterIdx', gutterIdx);
                console.log('allSizes in %', allSizes);
                setSizes(allSizes);
            }
        if(e.altKey && e.shiftKey && e.key==="+") {
            console.log("SplitRight");
            document.getElementById("sRight").style.display = "";

        }
    }
    return (
        <div className='container'>
            <ReactSplit
                    direction={SplitDirection.Horizontal}
                    gutterClassName="custom-gutter"
                    draggerClassName="custom-dragger"
                    minWidths={[200, 200, 10]}
                    onResizeFinished={handleResize}
                    initialSizes={sizes}
                >
                <Term/>
                <Term/>
            </ReactSplit>
        </div>
    )
}
