import React, {useCallback,useMemo,useState} from 'react'
import {useDropzone} from 'react-dropzone'
import "./dropfileinput.css"


const baseStyle = {
  flex: 1,
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  padding: '150px',
  borderWidth: 5,
  borderRadius: 30,
  borderColor: '#ad1717',
  borderStyle: 'inset none none inset',
  background: 'transparent',
  outline: 'none',
  transition: 'border .24s ease-in-out',
  margin:'25px 50px'
};

const focusedStyle = {
  borderColor: '#17ada3'
};

const acceptStyle = {
  borderColor: '#036133'
};

const rejectStyle = {
  borderColor: '#ff1744'
};

function dropfileinput({props}) {
  const { 
    getRootProps, 
    getInputProps, 
    acceptedFiles,
    isFocused,
    isDragAccept,
    isDragReject} = useDropzone({multiple: true });

  const style = useMemo(() => ({
      ...baseStyle,
      ...(isFocused ? focusedStyle : {}),
      ...(isDragAccept ? acceptStyle : {}),
      ...(isDragReject ? rejectStyle : {})
    }), [
      isFocused,
      isDragAccept,
      isDragReject
  ]);
  const [FileData,setFileData] = useState();
  const files = acceptedFiles.map((file) => {
    const reader = new FileReader();
    reader.readAsDataURL(acceptedFiles[0]);
    reader.onabort = () => console.log("file reading was aborted");
    reader.onerror = () => console.log("file reading has failed");
    reader.onload = (event) => {
      const fileContent = reader.result;
      setFileData(fileContent);
    };
    
    const fileType = file.type.split('/')[1];
    const isImg = ['png','jpeg','jpg'].includes(fileType);
    const isAudio = ['mp3','wave'].includes(fileType);
    const isText = fileType == 'txt';
    const isPython = fileType == 'py';
    const isVedio = fileType == 'mp4';
    return(
      <li key={file.path}>
        {isImg ? (
          <div>
            <span>üñºÔ∏è</span>
            {file.path} - {file.size} bytes
            <button className='remove' onClick={"#"}>X</button>
          </div>
        ) : (
          <div>
            {/* {file.path} - {file.size} bytes */}
          </div>
        )}
        {isAudio ? (
          <div>
            <span>üîâ</span>
            {file.path} - {file.size} bytes
            <button className='remove' onClick={"#"}>X</button>
          </div>
        ) : (
          <div>
            {/* {file.path} - {file.size} bytes */}
          </div>
        )}
        {isText ? (
          <div>
            <span>üìÑ</span>
            {file.path} - {file.size} bytes
            <button className='remove' onClick={"#"}>X</button>
          </div>
        ) : (
          <div>
            {/* {file.path} - {file.size} bytes */}
          </div>
        )}
        {isPython ? (
          <div>
            <span>üêç</span>
            {file.path} - {file.size} bytes
            <button className='remove' onClick={"#"}>X</button>
          </div>
        ) : (
          <div>
            {/* {file.path} - {file.size} bytes */}
          </div>
        )}
        {isVedio ? (
          <div>
            <span>üéûÔ∏è</span>
            {file.path} - {file.size} bytes
            <button className='remove' onClick={"#"}>X</button>
          </div>
        ) : (
          <div>
            {file.path} - {file.size} bytes
          </div>
        )}
      </li>
    );
  });
  function Uplorded(){
    alert("File uplord Compleate")
  }
  function handleClick() {
    console.log("Uploading....")
    chrome.runtime.sendMessage({
      type:'uplordfiledata',
      value: 'upfd'+FileData
    });
    setTimeout(Uplorded,1050)
    // add your function logic here
  }
  
  return (
   <>
    <div {...getRootProps({style})}>
      <input className="input-zone" {...getInputProps()} />
      <div className="text-center">
        <div className='logo'>
         <img className="logoimg" src='data:image/png;base64,'/>
        </div>
    </div>
    <div>
      <p className="dropzone-content">
          Drag‚Äôn‚Äôdrop some files here, or click to select files
      </p>
      <p style={{color:'red'}}><span style={{color:'green'}}><b>Peacü¶öck 0.1</b></span> only support text file</p>
    </div>
    </div>
    <div className='Uplordbutton'>
      <button className="ubutton" onClick={handleClick}>
        <img className='uplordicon' src={`data:image/png;base64,`}/>
      <span className="submit"> Uplord File</span>
    </button>
    </div>
    <aside className='drop-file-preview__item'>
        <ul>{files}</ul>
      </aside>
   </>
  );
}
export default dropfileinput